// Process Tautulli watch history for raw_media_activity table
const response = $input.first().json;

if (!response || !response.response || !response.response.data || !response.response.data.data) {
  return [{ json: { error: 'No watch history data found', raw: response } }];
}

const historyItems = response.response.data.data;
const now = new Date().toISOString();

const records = historyItems.map(item => {
  // Determine media type
  let mediaType = item.media_type || 'unknown';
  if (mediaType === 'episode') mediaType = 'tv_episode';
  if (mediaType === 'movie') mediaType = 'movie';

  // Convert duration from seconds
  const durationSeconds = item.duration || 0;

  // Convert watched_at timestamp (Tautulli uses Unix timestamps)
  const watchedAt = item.stopped ? new Date(item.stopped * 1000).toISOString() : now;

  return {
    json: {
      source: 'plex',
      user_name: item.user || item.friendly_name || 'unknown',
      media_type: mediaType,
      title: item.title || item.full_title || 'Unknown',
      series_title: item.grandparent_title || item.parent_title || null,
      duration_seconds: durationSeconds,
      watched_at: watchedAt,
      inserted_at: now,
      metadata: JSON.stringify({
        rating_key: item.rating_key,
        parent_rating_key: item.parent_rating_key,
        grandparent_rating_key: item.grandparent_rating_key,
        year: item.year,
        platform: item.platform,
        player: item.player,
        product: item.product,
        ip_address: item.ip_address,
        percent_complete: item.percent_complete,
        watched_status: item.watched_status,
        session_key: item.session_key
      })
    }
  };
});

return records;

// Process Tautulli library data for raw_media_library table
const response = $input.first().json;

if (!response || !response.response || !response.response.data) {
  return [{ json: { error: 'No library data found', raw: response } }];
}

const libraries = response.response.data;
const now = new Date().toISOString();

const records = libraries.map(lib => {
  // Determine media type from section_type
  let mediaType = lib.section_type || 'unknown';
  if (mediaType === 'show') mediaType = 'tv_show';

  return {
    json: {
      source: 'plex',
      media_type: mediaType,
      library_name: lib.section_name || 'Unknown Library',
      item_count: parseInt(lib.count) || 0,
      total_size_bytes: null,
      recorded_at: now,
      inserted_at: now,
      metadata: JSON.stringify({
        section_id: lib.section_id,
        section_type: lib.section_type,
        parent_count: lib.parent_count,
        child_count: lib.child_count,
        thumb: lib.thumb,
        art: lib.art,
        is_active: lib.is_active
      })
    }
  };
});

return records;
